import React, { Component } from 'react';
import Note from "./Note";
import Header from '../components/Navbar';
import {firestore,auth} from '../firebase/fire';
const setupNotes=(data)=>{
  if(data.length){
    const note=data.map(doc=>doc.data() )
    {
    <div className="card border-success mb-3" style={{maxWidth: "20rem"}}>
    
    <div className="card-body">
      <h4 className="card-title">$(note.note)</h4>
      
    </div>
    </div>
    }
  }
  else{
    <div><h4>You Are Not Logged In!</h4></div>
  }
  }
class Home extends Component {

  constructor(props) {
    super(props);
    this.state= {
      noteText: '',
      notes: [],
    }
  }

  updateNoteText(noteText){
    this.setState({noteText: noteText.target.value})
  }
  
  addNote() {
    if (this.state.noteText === '') {return}
    let noteArr = this.state.notes;
    noteArr.push(this.state.noteText);
    this.setState({ noteText: ''});
    this.textInput.focus();

  }

  handleKeyPress = (event) => {
    if(event.key === "Enter"){
      let noteArr = this.state.notes;
      noteArr.push(this.state.noteText);
      this.setState({ noteText: ''});
    }
  }

  deleteNote (index){
    let noteArr = this.state.notes;
    noteArr.splice(index, 1);
    this.setState({ notes: noteArr })
  }

  render() {

    auth.onAuthStateChanged(user => {
      if (user) {
        console.log('user logged in: ', user);
        db.collection('guides').get().then(querySnapshot => {
          setupNotes(querySnapshot.docs);
        });
      } else {
        console.log('user logged out');
        setupNotes([]);
      }
    })

    return (
      <div>
         
          <Header/>
        
        {notes}
        <div className="container">
        <div className="button " onClick={this.addNote.bind(this)} style ={{fontStyle:"oblique",marginBottom:"10px",marginTop:"80px", color:"#E54E65"}}>Add note</div>
        <input  style={{backgroundColor:"#E2CEEE"}} placeholder="Enter Notes" type="text" className="input "
        ref={((input) => {this.textInput = input})}
        value={this.state.noteText}
        onChange={noteText => this.updateNoteText(noteText)}
        onKeyPress={this.handleKeyPress.bind(this)}
        />
        </div>
      </div>
    );
  }
}

export default Home;
