import React, { Component } from 'react';
import {CardBody,CardText} from 'reactstrap';
class Card extends Component{

        deleteTodo(id) {
                firestore.collection('todos').doc(id).delete();
              }
    
           
        render (){
          
                
                return (

                       
                        <div className="card text-dark mb-3 mt-4 ml-3" style={{maxWidth: "50rem",backgroundColor:"#e6ff99"}}>
                        <div className="card-content">
                                <div className="level">
                                <div className="level-left">
                                <div className="level-item">
                        <div className="card-body">
                         
                     <p className="card-text "><h3>{this.props.notes.todo}</h3></p>
               
                        </div>
                      </div>
                      </div>
                      <div className="level-right">
                      <div className="level-item">
                     <button type="button" onClick={this.delete(this.props.id[0])} className="btn btn-danger" style={{float:"right"}}>Delete</button>
                 </div>
                 </div>
                 </div>
                 </div>
                 </div>
                );
        }
}
export default Card;
