import React, { Component } from 'react';
import {CardBody,CardText} from 'reactstrap';
import {firestore} from '../firebase/fire';
  
class Card extends Component{

        deleteTodo () {
                firestore.collection('todos').where('todo'==='add').delete();
                } 
    
           
        render (){
          const todos=this.props.notes.forEach(todo => {
                <div className="card text-dark mb-3 mt-4 ml-3" style={{maxWidth: "50rem",backgroundColor:"#e6ff99"}}>
                <div className="card-content">
                        <div className="level">
                        <div className="level-left">
                        <div className="level-item">
                <div className="card-body">
                 
             <p className="card-text "><h3>{todo}</h3></p>
       
                </div>
              </div>
              </div>
              <div className="level-right">
              <div className="level-item">
             <button type="button"    className="btn btn-danger" style={{float:"right"}}>Delete</button>
         </div>
         </div>
         </div>
         </div>
         </div>     
          });
           
                return (

                  {todos}    
                       
                );
        }
}
export default Card;
